import React, { useEffect, useRef, useState } from 'react';
import './Chat.css';

//Chat server
import io from 'socket.io-client';

//Uploads
import {uploads} from '../utils/config'

//Icons
import { BsX, BsChatDots } from 'react-icons/bs'

//Redux
import { useDispatch, useSelector } from 'react-redux';
import { profile } from '../slices/userSlice';

const Chat = () => {
  const dispatch = useDispatch();

  const {user} = useSelector((state) => state.user);

  const chatMessagesRef = useRef(null);

  const [socket, setSocket] = useState(null);

  const [isOpen, setIsOpen] = useState(false);
  const [unreadMessagesCount, setUnreadMessagesCount] = useState(1);
  const [chatMessages, setChatMessages] = useState({});
  const [messageId, setMessageId] = useState(null)
  const [newMessage, setNewMessage] = useState(null);
  const [userId, setUserId] = useState(null);
  
  useEffect(() => {
    profile();
  }, [dispatch])
  
  //Create and set the socket variable, and sets the disconnection from socket when the component is dismounted
  useEffect(() => {
    const newSocket = io('http://localhost:4000', {
      withCredentials: true,
    });
    setSocket(newSocket);

    return () => { //Disconnect from server when Chat.js is dismounted
      if (socket) {
        socket.disconnect();
      }
    };
  }, []);

  //Set the userId variable when the store returns the user object
  useEffect(() => {
    if (user) {
      setUserId(user.id);
    }
  }, [user]);

  //Joins the client in the server when the socket and the userId are ready
  useEffect(() => {
    if (userId && socket) {
      socket.emit('join', userId);
    }
  }, [userId, socket]);

  //Emits the command to search for messages and sets the manipulator to when messages are received
  useEffect(() => {
    if (userId && socket) {
      socket.emit('get-messages');
      socket.on('messages', (messages) => {
        setChatMessages(messages);
      });
    } 

    return () => { //Sets the manipulator off when the component gets dismounted
      if (socket) {
        socket.off('messages');

      }
    };
  }, [userId, socket]);

  //Toggle open/close chat
  const toggleChat = () => {
    setIsOpen(!isOpen);
  };

  //Scrolls the chat till the end when it opens
  useEffect(() => {
    if (isOpen) {
      chatMessagesRef.current.scrollTop = chatMessagesRef.current.scrollHeight;
    }
  }, [isOpen]);

  useEffect(() => {
    setMessageId('-NfHe4i_uod67B0toAgQ')
    console.log("Mensagens: ", chatMessages);
    console.log("Mensagem : ", chatMessages[messageId])
  }, [chatMessages])

  //Handles a message submit from the message input
  const handleMessageChange = (e) => {
    setNewMessage(e.target.value);
  };

  //Handles a message submit from the message input
  const handleMessageSubmit = (e) => {
    if(socket && userId) {
      const userSend = userId;
      const targetUser = "-NfHe4i_uod67B0toAgQ";

      socket.emit('send-message', { message: newMessage, targetUser: targetUser});

      setNewMessage(null)
    }
  };

  const getDateFromMilliseconds = (stringMilliseconds) => {
    // Converta a string para um número (milissegundos)
    const milliseconds = parseInt(stringMilliseconds, 10);

    // Crie um objeto de data a partir dos milissegundos
    const date = new Date(milissegundos);

    // Extraia o dia, mês e ano da data
    const dia = date.getDate();
    const mes = date.getMonth() + 1; // Mês é base 0, então adicionamos 1
    const ano = date.getFullYear();

    // Formate a data no estilo brasileiro (dd/mm/aaaa)
    const dataFormatada = `${dia}/${mes}/${ano}`;

    console.log(dataFormatada); // Saída: "24/10/2023" (por exemplo)
  }

  return (
    <div id='chat-component' className={`${isOpen ? 'open' : 'closed'}`}>
      {isOpen ? (
        <div id="chat-window">
          <button id="chat-close-button" onClick={toggleChat}>
            <BsX/>
          </button>
          <div id='chat-list'>
            <ul id='chat-players'>
              <li className="chat-players-item">
                <img src={`${uploads}/users/1698027263940.png`} alt={'user-1-photo'} className="chat-players-item-image" />
              </li>
              <li className="chat-players-item">
                <img src={`${uploads}/users/1698027243249.png`} alt={'user-1-photo'} className="chat-players-item-image" />
              </li>
              <li className="chat-players-item">
                <img src={`${uploads}/users/1698027263940.png`} alt={'user-1-photo'} className="chat-players-item-image" />
              </li>
            </ul>
            <div id="current-chat">
              <div id='current-chat-label'>
                ZacZoubar
              </div>
              <ul id="chat-messages" ref={chatMessagesRef}>
                {chatMessages && chatMessages[messageId] && Object.keys(chatMessages[messageId]).map((key, index, messageIds) => (
                  <li
                    key={key}
                    className={`chat-item ${chatMessages[messageId][key].sender === userId ? 'current-user-message' : 'other-user-message'}
                    ${index < messageIds.length - 1 && messageIds[index + 1] !== userId ? 'chat-item-border-bottom' : ''}`}
                  >
                    <p>{getDateFromMilliseconds(key)}</p>
                    {chatMessages[messageId][key].message}
                  </li>
                ))}
              </ul>
              <input
                type="text"
                id='message-input'
                value={newMessage || ''}
                placeholder='Digite aqui...'
                onChange={handleMessageChange}
                onKeyDown={(e) => {
                  if (e.key === 'Enter') {
                    e.preventDefault(); // Prevent the default form submission
                    handleMessageSubmit(); // Call your submit function here
                  }
                }}
              />
            </div>
          </div>
        </div>
      ) : (
        <button id="chat-button" onClick={toggleChat}>
          <BsChatDots id='chat-icon'/>
          <div className='chat-unread-messages-count'>
            {unreadMessagesCount}
          </div>
        </button>
      )}
    </div>
  );
};

export default Chat;
