import React from 'react'
import "./Navbar.css";

//components
import { NavLink, Link } from 'react-router-dom'
import { BsHouseDoorFill, BsFillPersonFill, BsBellFill } from 'react-icons/bs'

//hooks
import { useAuth } from '../hooks/useAuth';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { useEffect, useState } from 'react';

//redux
import {logout, reset} from '../slices/authSlice'
import { listNotifications } from '../slices/notificationSlice';

const Navbar = () => {
  const {auth} = useAuth();
  const {user} = useSelector((state) => state.auth);
  const {notifications} = useSelector((state) => state.notification);

  const [notificationList, setNotificationList] = useState(null);

  const navigate = useNavigate();

  const dispatch = useDispatch();

  const handleLogout = () => {
    dispatch(logout());
    dispatch(reset());

    navigate("/login");
  }

  useEffect(() => {
    if(user) {
      dispatch(listNotifications(user._id));
    }
  }, [dispatch, user]);

  useEffect(() => {
    if(notifications) {
      setNotificationList(notifications)
      console.log("notifications:", notifications);
      console.log("notificationList:", notificationList);
      console.log("nofiticationList != null: ", notificationList != null)
    }
  }, [notifications]);


  return <nav id='nav'>
    <div className='btn-logo'>
      <Link to='/'>Mate.gg</Link>
    </div>
    <ul id='nav-links'>
      {auth ? (
        <>
          <li>
            <NavLink to='/'>
              <BsHouseDoorFill/>
            </NavLink>
          </li>
          <li>
            <NavLink to="/profile">
              <BsFillPersonFill/>
            </NavLink>
          </li>
          {notificationList !== null 
            ? (<li>
                <NavLink to="/profile">
                  <BsBellFill />
                  <span>{notificationList.length}</span>
                </NavLink>
                <ul>
                  
                </ul>
              </li>) 
            : (
              <BsBellFill/>
            )}
          <li>
            <span onClick={handleLogout}>
              Sair
            </span>
          </li>
        </>
      ) : (
        <>
          <li>
            <NavLink to='/login'>
              Entrar
            </NavLink>
          </li>
          <li>
            <NavLink to='/register'>
              Cadastrar-se
            </NavLink>
          </li>
        </>
      )}
    </ul>
  </nav>
}

export default Navbar
