import React from 'react'
import './Home.css'

import {uploads} from '../../utils/config'

//Hooks
import { useSelector, useDispatch } from 'react-redux';
import { useState, useEffect } from 'react';

//Redux
import { listGames } from '../../slices/gameSlice';

const Home = () => {
  const dispatch = useDispatch();

  const {games, message, error, loading} = useSelector((state) => state.game);

  const [gameList, setGameList] = useState(undefined);

  useEffect(() => {
    dispatch(listGames());
  }, [dispatch]);

  useEffect(() => {
    if(Array.isArray(games)){
      console.log("É UM ARRAY!");
    } else {
      console.log("NÃO É UM ARRAY!");
    }
    if(games) {
      setGameList(games);
      console.log(games);
    }
  }, [games]);

  return (
    <div id='home'>
      <div className='homepage-infowindow'>
        <img className="homepage-image" src={`${uploads}/homepage_image/homepage_image.png`} alt={"homepage_image"}/>
        <h2 className='homepage-title'>Encontre a parceria de gameplay ideal</h2>
      </div>
      <div className='game-list'>
        {gameList && gameList.map((game) => (
          <div key={game.id} className="game-item" style={{ backgroundImage: `${uploads}/games/${game.splashart}` }}>
            <h3>{game.name}</h3>
          </div>
        ))}
      </div>
    </div>
  )
}

export default Home
