import React from 'react';
import './GameRegister.css';

//Hooks
import { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';

//Redux
import { getUserGames, resetMessage, updateProfile } from '../../slices/userSlice'
import { getGames } from '../../slices/gameSlice';

//Components
import Message from '../../components/Message'

//Router
import { useParams } from 'react-router-dom';

const GameRegister = () => {
  //Get the game id from the url (eft, lol, dayz)
  const { game } = useParams();

  const dispatch = useDispatch();

  //Get the current user games and his games infos
  const { userGames, loading, error, message } = useSelector((state) => state.user);
  //Get the list of games of the website
  const { games } = useSelector((state) => state.game);

  //The current user's current game info
  const [userGameInfo, setUserGameInfo] = useState(null);
  //The current game fields to register or update
  const [gameFields, setGameFields] = useState(null);
  //The current game name (Escape From Tarkov, League of Legends, DayZ)
  const [gameName, setGameName] = useState("");
  //The labels to the fields of the game
  const [labels, setLabels] = useState(null);

  //The objectified formData, that will be turned into a real FormData to be sent to register/update
  const [formData, setFormData] = useState({});

  //Load user data for the game
  useEffect(() => {
    dispatch(getUserGames())
    dispatch(getGames())
  }, [dispatch]);

  useEffect(() => {
    if(games && game) {
      if(games[game] && games[game].name)
      setGameName(games[game].name);
      setLabels(games["labels"])
    }
  }, [games, game]);

  //Fill userGames with the game from his list of games
  useEffect(() => {
    if(userGames && game) {
      const userGameInfo = userGames[game];
      if(userGameInfo) {
        setUserGameInfo(userGameInfo);
      }
    }
  }, [userGames, game]);

  //Fill user game info to edit (if exists), and fill the gameInfo needed to create the form and register
  useEffect(() => {
    const gameInfo = userGames[game];
    if(gameInfo) {
      setUserGameInfo(gameInfo);
    }
    if(games) {
      const fields = games[game];
      if(fields && fields.userInfo) {
        const userInfo = fields.userInfo;
        setGameFields(userInfo)
      }
    }
    console.log("Game: ", game);
    console.log("userGames: ", userGames);
    console.log("games: ", games);
    console.log("userGameInfo: ", userGameInfo);
    console.log("gameFields: ", gameFields);
  }, [games]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleNumberChange = (e) => {
    const { name, value } = e.target;
  
    if (!isNaN(value) && parseFloat(value) >= 0) {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    console.log(formData)
    // const userFormData = Object.keys(userGameInfo).forEach((key) =>
    //   formData.append(key, userGameInfo[key])
    // );

    // formData.append("user", userFormData);

    // await dispatch(updateProfile(formData));

    // setTimeout(() => {
    //   dispatch(resetMessage());
    // }, 2000)
  }

  return (
    <div id='edit-profile'>
      <h2>Perfil de jogo: {gameName}</h2>
      <p className='subtitle'>Informe ou altere os dados de sua conta de jogo.</p>
      <form onSubmit={handleSubmit}>
        {gameFields && Object.entries(gameFields).map(([fieldName, field], key) => (
          <div key={key}>
            <label>
              {fieldName === 'dailyOnlineTime' ? (
                <div id='dailyOnlineTime'>
                  <div className='time-selection'>
                    <span>Quando normalmente começa a jogar:</span>
                    <div className='time-selector'>
                      <select
                        id={`${fieldName}-startHour`}
                        name={`${fieldName}-startHour`}
                        value={formData[`${fieldName}-startHour`] || ''}
                        onChange={handleChange}
                      >
                        {Array.from({ length: 24 }, (_, i) => i).map((hour) => (
                          <option key={hour} value={hour}>
                            {hour < 10 ? `0${hour}` : hour}
                          </option>
                        ))}
                      </select>
                      <span> : </span>
                      <select
                        id={`${fieldName}-startMinute`}
                        name={`${fieldName}-startMinute`}
                        value={formData[`${fieldName}-startMinute`] || ''}
                        onChange={handleChange}
                      >
                        {Array.from({ length: 60 }, (_, i) => i).map((minute) => (
                          <option key={minute} value={minute}>
                            {minute < 10 ? `0${minute}` : minute}
                          </option>
                        ))}
                      </select>
                    </div>
                  </div>
                  <div className='time-selection'>
                    <span>Quando normalmente para de jogar:</span>
                    <div className='time-selector'>
                      <select
                        id={`${fieldName}-endHour`}
                        name={`${fieldName}-endHour`}
                        value={formData[`${fieldName}-endHour`] || ''}
                        onChange={handleChange}
                      >
                        {Array.from({ length: 24 }, (_, i) => i).map((hour) => (
                          <option key={hour} value={hour}>
                            {hour < 10 ? `0${hour}` : hour}
                          </option>
                        ))}
                      </select>
                      <span> : </span>
                      <select
                        id={`${fieldName}-endMinute`}
                        name={`${fieldName}-endMinute`}
                        value={formData[`${fieldName}-endMinute`] || ''}
                        onChange={handleChange}
                      >
                        {Array.from({ length: 60 }, (_, i) => i).map((minute) => (
                          <option key={minute} value={minute}>
                            {minute < 10 ? `0${minute}` : minute}
                          </option>
                        ))}
                      </select>
                    </div>
                  </div>
                </div>
              ) : field === 'Integer' ? (
                <input
                  type="number"
                  id={fieldName}
                  name={fieldName}
                  value={formData[fieldName] || ''}
                  onChange={handleNumberChange}
                />
              ) : field === 'String' ? (
                <input
                  type="text"
                  id={fieldName}
                  name={fieldName}
                  value={formData[fieldName] || ''}
                  onChange={handleChange}
                />
              ) : typeof field == typeof [] ? (
                <select
                  id={fieldName}
                  name={fieldName}
                  value={formData[fieldName] || ''}
                  onChange={handleChange}
                >
                  {Object.values(field).map((value) => (
                    <option key={value} value={value}>
                      {value}
                    </option>
                  ))}
                </select>
              ) : (
                <p>Unsupported field type</p>
              )}
            </label>
          </div>
        ))}
        {!loading && !userGames[game] && <input type="submit" value="Registrar" />}
        {!loading && userGames[game] && <input type="submit" value="Salvar" />}
        {loading && <input type="submit" disabled value="Aguarde..." />}
        {error && <Message msg={error} type="error" />}
        {message && <Message msg={message} type="success" />}
      </form>
    </div>
  )
}

export default GameRegister;
