import React from 'react';
import './GamePage.css';

import {uploads} from '../../utils/config'

import { BsJoystick } from 'react-icons/bs'

//Hooks
import { useSelector, useDispatch } from 'react-redux';
import { useState, useEffect } from 'react';

//Redux
import { getGames } from '../../slices/gameSlice';
import { getUserGames, profile } from '../../slices/userSlice'

//router
import { useNavigate, useParams } from 'react-router-dom';

const GamePage = () => {
  //Predefined functions
  const dispatch = useDispatch();
  const navigate = useNavigate();

  //Params
  const { game } = useParams();

  //Store
  const {games} = useSelector((state) => state.game);
  const {players} = useSelector((state) => state.user)
  
  //useState
  const [gameInfo, setGameInfo] = useState(null);
  const [gamePlayers, setGamePlayers] = useState(null);

  //Load game and users data for the list of players
  useEffect(() => {
    //dispatch(getGamePlayers())
    dispatch(getGames())
  }, [dispatch]);

  useEffect(() => {
    if(games && games[game]) {
      setGameInfo(games[game]);
      console.log("gameInfo: ", gameInfo)
    }
  }, [games])

  useEffect(() => {
    if(gamePlayers) {
      //set
    }
  }, [gamePlayers])

  const handleProfileButtonClick = async (e) => {
    e.preventDefault();

    return navigate(`/gameRegister/${game}`);
  }

  return (
    <div className="game-page">
      <div className="user-profile-button" onClick={handleProfileButtonClick}>
        <button><BsJoystick/>Perfil de jogador</button>
      </div>
      {gameInfo ? (
        <div>
          <img
            className="background-image"
            src={`${uploads}/games/${gameInfo.splashart}`}
            alt="background"
          />
          <div className="game-list">
            <div className='game-list-title'>
              <h2>{gameInfo.name}</h2>
            </div>
            <div className='player-list'>

            </div>
          </div>
        </div>
      ) : (
        <p>Carregando jogos...</p>
      )}
    </div>
  );
};

export default GamePage;
