import React from 'react';
import './GamePage.css';

import {uploads} from '../../utils/config'

//Hooks
import { useSelector, useDispatch } from 'react-redux';
import { useState, useEffect } from 'react';

//Redux
import { getGames } from '../../slices/gameSlice';
import { getUserGames, profile } from '../../slices/userSlice'

//router
import { useParams } from 'react-router-dom';

const GamePage = () => {
  const { game } = useParams();

  const [gameInfo, setGameInfo] = useState(null);

  const dispatch = useDispatch();
  
  const {games} = useSelector((state) => state.game);
  const { user } = useSelector((state) => state.user);

  //Load game and users data for the list of players
  useEffect(() => {
    dispatch(getGames())
    dispatch(profile())
  }, [dispatch]);

  useEffect(() => {
    if(games && games[game]) {
      setGameInfo(games[game]);
      console.log("gameInfo: ", gameInfo)
    }
  }, [games])

  return (
    <div className="game-page">
      {gameInfo !== null ? (
        <div>
          <img
            className="background-image"
            src={`${uploads}/games/${gameInfo[game]}`}
            alt="background"
          />
          <div className="game-list">
            {/* Seu componente de lista aqui */}
          </div>
        </div>
      ) : (
        <p>Carregando jogos...</p>
      )}
    </div>
  );
};

export default GamePage;
