import React from 'react';
import './EditProfile.css';

import {uploads} from '../../utils/config'

//Hooks
import { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';

//Redux
import { profile, resetMessage } from '../../slices/userSlice'

//Components
import Message from '../../components/Message'

const EditProfile = () => {
  const dispatch = useDispatch();

  const {user, message, error, loading} = useSelector((state) => state.user)

  const [email, setEmail] = useState("");
  const [region, setRegion] = useState("");
  const [birthDate, setBirthDate] = useState("");
  const [password, setPassword] = useState("");
  const [profileImage, setProfileImage] = useState("");
  const [previewImage, setPreviewImage] = useState("");

  //Load user data
  useEffect(() => {
    dispatch(profile());
  }, [dispatch]);

  //Fill form with user data
  useEffect(() => {
    if(user) {
      setEmail(user.email);
      setRegion(user.region);
      setBirthDate(user.birth_date)
    }
  }, [user])

  console.log(user);

  const handleSubmit = (e) => {
    e.preventDefault();
  }

  const handleFile = () => {
    //Make image preview
    const image = e.target.files[0];

    setPreviewImage(image);

    //Update image state
    setProfileImage(image);
  }

  return (
    <div id='edit-profile'>
      <h2>Edição de perfil</h2>
      <p className='subtitle'>Adicione uma imagem de perfil ou altere alguma informação.</p>
      {(user.profileImage || previewImage) && (
        <img
          src={
            previewImage ? URL.createObjectURL(previewImage) : `${uploads}/users/${user.profileImage}`
          }
          alt={user.name}
        />
      )}
      <form onSubmit={handleSubmit}>
        <input type="email" placeholder='E-mail' disabled value={email || ""}/>
        <input type="date" placeholder='Data de nascimento' onChange={(e) => setBirthDate(e.target.value)} value={birthDate || ""}/>
        <select name="regiao" id="regiao" onChange={(e) => setRegion(e.target.value)} value={region || ""}>
          <option value="">N/A</option>
          <option value="Norte">Norte</option>
          <option value="Nordeste">Nordeste</option>
          <option value="Sudeste">Sudeste</option>
          <option value="Sul">Sul</option>
          <option value="Centro-Oeste">Centro-Oeste</option>
        </select>
        <label>
          <span>Imagem de perfil:</span>
          <input type="file" onChange={handleFile}/>
        </label>
        <label>
          <span>Deseja alterar a senha?</span>
          <input type="password" placeholder='Digite a nova senha' onChange={(e) => setPassword(e.target.value)} value={password || ""}/>
        </label>
        <input type="submit" value="Salvar"/>
      </form>
    </div>
  )
}

export default EditProfile
