import React from 'react'
import "./Navbar.css";

//components
import { NavLink, Link } from 'react-router-dom'
import { BsHouseDoorFill, BsFillPersonFill, BsBellFill } from 'react-icons/bs'

//hooks
import { useAuth } from '../hooks/useAuth';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { useEffect } from 'react';

//redux
import {logout, reset} from '../slices/authSlice'

const Navbar = () => {
  const {auth} = useAuth();
  const {user} = useSelector((state) => state.auth);

  const navigate = useNavigate();

  const dispatch = useDispatch();

  const handleLogout = () => {
    dispatch(logout());
    dispatch(reset());

    navigate("/login");
  }

  useEffect(() => {
    if(user) {
      console.log(user);
    }
    // if(user) {
    //   const notificationsRef = firebase.database().ref(`usuarios/${user}/notifications`);

    //   notificationsRef.on('value', (snapshot) => {
    //     const data = snapshot.val();
    //     if (data) {
    //       const notificacoesArray = Object.values(data);
    //       setNotifications(notificacoesArray);
    //     } else {
    //       setNotifications([]);
    //     }
    //   });

    //   return () => {
    //     notificationsRef.off('value');
    //   };
    // }
  }, []);

  return <nav id='nav'>
    <div className='btn-logo'>
      <Link to='/'>Mate.gg</Link>
    </div>
    <ul id='nav-links'>
      {auth ? (
        <>
          <li>
            <NavLink to='/'>
              <BsHouseDoorFill/>
            </NavLink>
          </li>
          <li>
            <NavLink to="/profile">
              <BsFillPersonFill/>
            </NavLink>
          </li>
          <li>
            <NavLink to="/profile">
              <BsBellFill/>
            </NavLink>
          </li>
          <li>
            <span onClick={handleLogout}>
              Sair
            </span>
          </li>
        </>
      ) : (
        <>
          <li>
            <NavLink to='/login'>
              Entrar
            </NavLink>
          </li>
          <li>
            <NavLink to='/register'>
              Cadastrar-se
            </NavLink>
          </li>
        </>
      )}
    </ul>
  </nav>
}

export default Navbar
