import React from 'react'
import './Home.css'

import {uploads} from '../../utils/config'

//Hooks
import { useSelector, useDispatch } from 'react-redux';
import { useState, useEffect } from 'react';

//Redux
import { getGames } from '../../slices/gameSlice';
import { getUserGames, profile } from '../../slices/userSlice'

//router
import {Link} from 'react-router-dom';

const Home = () => {
  const dispatch = useDispatch();

  const {games} = useSelector((state) => state.game);
  const {user, userGames} = useSelector((state) => state.user);

  const [gameList, setGameList] = useState(null);
  const [userGamesList, setUserGamesList] = useState(null);

  useEffect(() => {
    dispatch(profile());
    dispatch(getGames());
    dispatch(getUserGames(user._id));
    console.log(user);
  }, [dispatch]);

  useEffect(() => {
    if(games) {
      setGameList(games);
    }
  }, [games]);

  useEffect(() => {
    // console.log("jogos do usuário: ");
    // console.log(userGames);
    if(userGames) {
      setUserGamesList(userGames);
    }
    // console.log("jogos: ", gameList);
    // console.log("user: ", user);
    // console.log("jogos do usuário: ");
    // console.log(userGames);
    // console.log(userGamesList);
  }, [userGames])

  return (
    <div id='home'>
      <div className='homepage-infowindow'>
        <img className="homepage-image" src={`${uploads}/homepage_image/homepage_image.png`} alt={"homepage_image"} width="5280" height="3468"/>
        <h2 className='homepage-title'>Encontre a parceria de gameplay ideal</h2>
      </div>
      <div className='game-list-label'>
        <p>
          Jogos disponíveis
        </p>
      </div>
      <div className='game-list'>
        {gameList !== null ? (
          Object.values(gameList).map((game) => (
            <div className="game-item" key={game.name}>
              <Link to={`/games/${encodeURIComponent(game.name)}`} className="game-item-link">
                <img src={`${uploads}/games/${game.splashart}`} alt={game.name} className="game-item-image" />
                <h3 className="game-item-title">{game.name}</h3>
              </Link>
            </div>
          ))
        ) : (
          <p>Carregando jogos...</p>
        )}
      </div>
    </div>
  )
}

export default Home
