import React from 'react';
import './GamePage.css';

import {uploads} from '../../utils/config'

import { BsJoystick } from 'react-icons/bs'

//Hooks
import { useSelector, useDispatch } from 'react-redux';
import { useState, useEffect } from 'react';

//Redux
import { getGames } from '../../slices/gameSlice';
import { getPlayers } from '../../slices/userSlice'

//router
import { useNavigate, useParams } from 'react-router-dom';

const GamePage = () => {
  //Predefined functions
  const dispatch = useDispatch();
  const navigate = useNavigate();

  //Params
  const { game } = useParams();

  //Store
  const {games} = useSelector((state) => state.game);
  const {players} = useSelector((state) => state.user);
  
  //useState
  const [gameInfo, setGameInfo] = useState(null);
  const [gamePlayers, setGamePlayers] = useState(null);
  const [gameLabels, setGameLabels] = useState(null);

  //Load game and users data for the list of players
  useEffect(() => {
    dispatch(getGames());
    dispatch(getPlayers(game));
  }, [dispatch, game]);

  useEffect(() => {
    if(games && games[game]) {
      setGameInfo(games[game]);
    }
  }, [games]);

  useEffect(() => {
    if(gameInfo) {
      console.log("gameInfo: ", gameInfo.userInfo)
      setGameLabels(gameInfo.userInfo)
    }
  })

  useEffect(() => {
    if(players) {
      setGamePlayers(players);
      console.log(gamePlayers)
    }
  }, [players]);

  const handleProfileButtonClick = async (e) => {
    e.preventDefault();

    return navigate(`/gameRegister/${game}`);
  }

  return (
    <div className="game-page">
      <div className="user-profile-button" onClick={handleProfileButtonClick}>
        <button><BsJoystick/> Perfil de jogador</button>
      </div>
      {gameInfo ? (
        <div>
          <img
            className="background-image"
            src={`${uploads}/games/${gameInfo.background}`}
            alt="background"
          />
          <div className='player-list'>
            <div className='player-list-title'>
              <h2>{gameInfo.name}</h2>
            </div>




            <div>
              <ul className='player-list-list'>
                {gameLabels && Object.keys(gameLabels).length > 0 ? (
                  Object.entries(gameLabels).map((label, index) => (
                    <li key={index} className="label-item">
                      <h3>fodase</h3>
                    </li>
                  ))
                ) : (
                  <p>Nenhuma label disponÃ­vel.</p>
                )}


                {gamePlayers && Object.keys(gamePlayers).length > 0 ? (
                  Object.entries(gamePlayers).map((player, index) => (
                    <li key={index} className="player-item">
                      {Object.entries(player).map((playerInfoItem, index) => (
                        <p>{playerInfoItem}</p>
                      ))}
                      <p>{player[1].description}</p>
                      {/* Renderize outras informaÃ§Ãµes dos jogadores conforme necessÃ¡rio */}
                    </li>
                  ))
                ) : (
                  <p>Nenhum jogador disponÃ­vel.</p>
                )}
              </ul>
            </div>
          </div>
        </div>
      ) : (
        <p>Carregando jogos...</p>
      )}
    </div>
  );
};

export default GamePage;
