import React from 'react';
import './GamePage.css';

import {uploads} from '../../utils/config'

import { BsJoystick } from 'react-icons/bs'

//Hooks
import { useSelector, useDispatch } from 'react-redux';
import { useState, useEffect } from 'react';

//Redux
import { getGames } from '../../slices/gameSlice';
import { getPlayers } from '../../slices/userSlice'

//router
import { useNavigate, useParams } from 'react-router-dom';

const GamePage = () => {
  //Predefined functions
  const dispatch = useDispatch();
  const navigate = useNavigate();

  //Params
  const { game } = useParams();

  //Store
  const {games} = useSelector((state) => state.game);
  const {players} = useSelector((state) => state.user);
  
  //useState
  const [gameInfo, setGameInfo] = useState(null);
  const [gamePlayers, setGamePlayers] = useState(null);
  const [gameLabels, setGameLabels] = useState(null);

  //Load game and users data for the list of players
  useEffect(() => {
    dispatch(getGames());
    dispatch(getPlayers(game));
  }, [dispatch, game]);

  useEffect(() => {
    if(games && games[game]) {
      setGameInfo(games[game]);
      setGameLabels(games.labels)
    }
  }, [games]);

  useEffect(() => {
    if(gameInfo) {
      console.log("gameInfo: ", gameInfo.userInfo)
      setGameLabels(gameInfo.userInfo)
    }
  })

  useEffect(() => {
    if(players) {
      setGamePlayers(players);
      console.log(gamePlayers)
    }
  }, [players]);

  const handleProfileButtonClick = async (e) => {
    e.preventDefault();

    return navigate(`/gameRegister/${game}`);
  }

  return (
    <div className="game-page">
      <div className="user-profile-button" onClick={handleProfileButtonClick}>
        <button><BsJoystick/> Perfil de jogador</button>
      </div>
      {gameInfo ? (
        <div>
          <img
            className="background-image"
            src={`${uploads}/games/${gameInfo.background}`}
            alt="background"
          />
          <div className='player-list'>
            <div className='player-list-title'>
              <h2>{gameInfo.name}</h2>
            </div>
            <div className='player-list-list'>
              <ul className='horizontal-list'>
                <li id='player-info-labels'>
                  {gameLabels ? (
                    Object.entries(gameLabels).map(([key]) => (
                      
                      <span key={key} className='player-info-labels-item'>{key}</span>
                    ))
                  ) : (
                    <p>Nenhuma label disponível.</p>
                  )}
                </li>
              </ul>
            </div>
          </div>
        </div>
      ) : (
        <p>Carregando jogos...</p>
      )}
    </div>
  );
};

export default GamePage;
