import React, { useEffect, useRef, useState } from 'react';
import './Chat.css';

//Chat server
import io from 'socket.io-client';

//Uploads
import {uploads} from '../utils/config'

//Icons
import { BsX, BsChatDots } from 'react-icons/bs'

//Redux
import { useDispatch, useSelector } from 'react-redux';
import { profile } from '../slices/userSlice';

const Chat = () => {
  const dispatch = useDispatch();

  const {user} = useSelector((state) => state.user);

  const chatMessagesRef = useRef(null);

  const [socket, setSocket] = useState(null);

  const [isOpen, setIsOpen] = useState(false);
  const [unreadMessagesCount, setUnreadMessagesCount] = useState(1);
  const [chatMessages, setChatMessages] = useState(null);
  const [newMessage, setNewMessage] = useState(null);
  const [userId, setUserId] = useState(null);
  
  //Create and set the socket variable, and sets the disconnection from socket when the component is dismounted
  useEffect(() => {
    const newSocket = io('http://localhost:4000', {
      withCredentials: true,
    });
    setSocket(newSocket);

    return () => { //Disconnect from server when Chat.js is dismounted
      if (socket) {
        socket.disconnect();
      }
    };
  }, []);

  useEffect(() => {
    profile();
  }, [dispatch])

  //Get the userId variable from the user object
  useEffect(() => {
    if(user) {
      setUserId(user.id)
    }
  }, [user])

  //Log user in the socket by the userId variable
  useEffect(() => {
    if (userId) {
      socket.emit('join', userId);
    }
  }, [userId])

  //Toggle open/close chat
  const toggleChat = () => {
    setIsOpen(!isOpen);
  };

  //Scrolls the chat till the end when it opens
  useEffect(() => {
    if (isOpen) {
      chatMessagesRef.current.scrollTop = chatMessagesRef.current.scrollHeight;
    }
  }, [isOpen]);

  //Handles a message submit from the message input
  const handleMessageChange = (e) => {
    setNewMessage(e.target.value);
  };

  //Handles a message submit from the message input
  const handleMessageSubmit = (e) => {

    setNewMessage(null)
  };

  return (
    <div id='chat-component' className={`${isOpen ? 'open' : 'closed'}`}>
      {isOpen ? (
        <div id="chat-window">
          <button id="chat-close-button" onClick={toggleChat}>
            <BsX/>
          </button>
          <div id='chat-list'>
            <ul id='chat-players'>
              <li className="chat-players-item">
                <img src={`${uploads}/users/1698027263940.png`} alt={'user-1-photo'} className="chat-players-item-image" />
              </li>
              <li className="chat-players-item">
                <img src={`${uploads}/users/1698027243249.png`} alt={'user-1-photo'} className="chat-players-item-image" />
              </li>
              <li className="chat-players-item">
                <img src={`${uploads}/users/1698027263940.png`} alt={'user-1-photo'} className="chat-players-item-image" />
              </li>
            </ul>
            <div id="current-chat">
              <div id='current-chat-label'>
                ZacZoubar
              </div>
              <ul id="chat-messages" ref={chatMessagesRef}>
                <li className="chat-item current-user-message chat-item-border-bottom">Mensagem 1</li>
                <li className="chat-item other-user-message chat-item-border-bottom">Mensagem 2</li>
                <li className="chat-item current-user-message">Mensagem 3</li>
                <li className="chat-item current-user-message chat-item-border-bottom">Mensagem 4</li>
                <li className="chat-item other-user-message">Mensagem 5</li>
                <li className="chat-item other-user-message">Mensagem 6</li>
                <li className="chat-item other-user-message chat-item-border-bottom">Mensagem a a absdjhaksd kjsdkjas dkjkjsa dkasdnksd  7</li>
                <li className="chat-item current-user-message chat-item-border-bottom">Mensagem 8</li>
                <li className="chat-item other-user-message">Mensagem 9</li>
                <li className="chat-item other-user-message chat-item-border-bottom">Mensagem 10</li>
                <li className="chat-item current-user-message">Mensagem 11</li>
                <li className="chat-item current-user-message chat-item-border-bottom">Mensagem 12</li>
              </ul>
              <input
                type="text"
                id='message-input'
                value={newMessage || ''}
                placeholder='Digite aqui...'
                onChange={handleMessageChange}
                onKeyDown={(e) => {
                  if (e.key === 'Enter') {
                    e.preventDefault(); // Prevent the default form submission
                    handleMessageSubmit(); // Call your submit function here
                  }
                }}
              />
            </div>
          </div>
        </div>
      ) : (
        <button id="chat-button" onClick={toggleChat}>
          <BsChatDots id='chat-icon'/>
          <div className='chat-unread-messages-count'>
            {unreadMessagesCount}
          </div>
        </button>
      )}
    </div>
  );
};

export default Chat;
