import React from 'react';
import './GamePage.css';

import {uploads} from '../../utils/config'

//Hooks
import { useSelector, useDispatch } from 'react-redux';
import { useState, useEffect } from 'react';

//Redux
import { getGames } from '../../slices/gameSlice';
import { getUserGames, profile } from '../../slices/userSlice'

//router
import { useParams } from 'react-router-dom';

const GamePage = () => {
  const { gameId } = useParams();
  console.log(gameId)

  const dispatch = useDispatch();
  
  const { games } = useSelector((state) => state.game);
  const { user } = useSelector((state) => state.user);

  //Load game and users data for the list of players
  useEffect(() => {
    dispatch(profile())
    dispatch(getGames())
    dispatch(profile());
    console.log("games: ", games)
    console.log("user: ", user)
  }, [dispatch]);

  return (
    <div className="game-page">
      {games !== null ? (
        <div>
          <img
            className="background-image"
            src={`${uploads}/games/${games[gameId].splashart}`}
            alt="background"
          />
          <div className="game-list">
            {/* Seu componente de lista aqui */}
          </div>
        </div>
      ) : (
        <p>Carregando jogos...</p>
      )}
    </div>
  );
};

export default GamePage;
