import React from 'react'
import "./Navbar.css";

//components
import { NavLink, Link } from 'react-router-dom'
import { BsHouseDoorFill, BsFillPersonFill, BsBellFill } from 'react-icons/bs'

//hooks
import { useAuth } from '../hooks/useAuth';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { useEffect } from 'react';

//redux
import {logout, reset} from '../slices/authSlice'
import { notifications } from '../slices/notificationSlice';

const Navbar = () => {
  const {auth} = useAuth();
  const {user} = useSelector((state) => state.auth);
  const {notificationList} = useSelector((state) => state.notification.notifications);

  const navigate = useNavigate();

  const dispatch = useDispatch();

  const handleLogout = () => {
    dispatch(logout());
    dispatch(reset());

    navigate("/login");
  }

  useEffect(() => {
    dispatch(notifications(user._id));
    console.log(notificationList)
    // notificationList.map((notification) => {
    //   console.log(notification);
    // })
  }, [user]);

  return <nav id='nav'>
    <div className='btn-logo'>
      <Link to='/'>Mate.gg</Link>
    </div>
    <ul id='nav-links'>
      {auth ? (
        <>
          <li>
            <NavLink to='/'>
              <BsHouseDoorFill/>
            </NavLink>
          </li>
          <li>
            <NavLink to="/profile">
              <BsFillPersonFill/>
            </NavLink>
          </li>
          <li>
            <NavLink to="/profile">
              <BsBellFill/>
            </NavLink>
          </li>
          <li>
            <span onClick={handleLogout}>
              Sair
            </span>
          </li>
        </>
      ) : (
        <>
          <li>
            <NavLink to='/login'>
              Entrar
            </NavLink>
          </li>
          <li>
            <NavLink to='/register'>
              Cadastrar-se
            </NavLink>
          </li>
        </>
      )}
    </ul>
  </nav>
}

export default Navbar
